import React, { useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  Button,
  IconButton,
  Menu,
  MenuItem,
  Drawer,
  List,
  ListItem,
  ListItemText,
  useTheme,
  useMediaQuery,
  Badge,
} from "@mui/material";
import {
  Menu as MenuIcon,
  ShoppingCart,
  ExpandMore,
} from "@mui/icons-material";

const menuItems = [
  { label: "Home", options: ["Main", "Landing"] },
  { label: "Events", options: ["All Events", "My Events"] },
  { label: "Calendar", options: ["Monthly", "Weekly"] },
  { label: "Speakers", options: ["Keynotes", "Workshops"] },
  { label: "Venues", options: ["Venue List"] },
  { label: "Schedules", options: ["Day 1", "Day 2"] },
  { label: "Pages", options: ["About", "Contact"] },
];

export default function Navbar() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("md"));
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [anchorEls, setAnchorEls] = useState({});

  const handleMenuOpen = (event, label) => {
    setAnchorEls({ ...anchorEls, [label]: event.currentTarget });
  };

  const handleMenuClose = (label) => {
    setAnchorEls({ ...anchorEls, [label]: null });
  };

  const toggleDrawer = (open) => () => {
    setDrawerOpen(open);
  };

  return (
    <>
      <AppBar
        position="static"
        sx={{ backgroundColor: "white", color: "black", boxShadow: "none" }}
      >
        <Toolbar sx={{ justifyContent: "space-between" }}>
          {/* Logo */}
          <Typography variant="h6" fontWeight="bold">
            EVENTCHAMP
          </Typography>

          {/* Desktop Menu */}
          {!isMobile && (
            <Box sx={{ display: "flex", gap: 2 }}>
              {menuItems.map(({ label, options }) => (
                <Box key={label}>
                  <Button
                    onClick={(e) => handleMenuOpen(e, label)}
                    endIcon={<ExpandMore />}
                    sx={{ color: "black" }}
                  >
                    {label}
                  </Button>
                  <Menu
                    anchorEl={anchorEls[label]}
                    open={Boolean(anchorEls[label])}
                    onClose={() => handleMenuClose(label)}
                  >
                    {options.map((option) => (
                      <MenuItem
                        key={option}
                        onClick={() => handleMenuClose(label)}
                      >
                        {option}
                      </MenuItem>
                    ))}
                  </Menu>
                </Box>
              ))}
            </Box>
          )}

          {/* Right Side (Cart + Auth Buttons or Hamburger) */}
          <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
            <IconButton>
              <Badge badgeContent={0} color="error">
                <ShoppingCart />
              </Badge>
            </IconButton>

            {!isMobile ? (
              <>
                <Button variant="outlined" sx={{ borderRadius: 5 }}>
                  Sign In
                </Button>
                <Button
                  variant="contained"
                  sx={{ borderRadius: 5, backgroundColor: "blueviolet" }}
                >
                  Sign Up
                </Button>
              </>
            ) : (
              <IconButton onClick={toggleDrawer(true)}>
                <MenuIcon />
              </IconButton>
            )}
          </Box>
        </Toolbar>
      </AppBar>

      {/* Drawer for Mobile */}
      <Drawer anchor="right" open={drawerOpen} onClose={toggleDrawer(false)}>
        <Box sx={{ width: 250, p: 2 }}>
          <Typography variant="h6" sx={{ mb: 2 }}>
            Menu
          </Typography>
          {menuItems.map(({ label, options }) => (
            <Box key={label} sx={{ mb: 1 }}>
              <Typography variant="subtitle1" fontWeight="bold">
                {label}
              </Typography>
              <List dense>
                {options.map((option) => (
                  <ListItem button key={option}>
                    <ListItemText primary={option} />
                  </ListItem>
                ))}
              </List>
            </Box>
          ))}
          <Button fullWidth variant="outlined" sx={{ mt: 2 }}>
            Sign In
          </Button>
          <Button
            fullWidth
            variant="contained"
            sx={{ mt: 1, backgroundColor: "blueviolet" }}
          >
            Sign Up
          </Button>
        </Box>
      </Drawer>
    </>
  );
}
