import React from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  IconButton,
  Badge,
  Button,
} from "@mui/material";
import { ShoppingCart } from "@mui/icons-material";
import {
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import { Link } from "react-router-dom";

export default function Header1() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("md"));
  const [drawerOpen, setDrawerOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setDrawerOpen((prev) => !prev);
  };

  return (
    <AppBar position="static" sx={{ justifyContent: "space-between" }}>
      <Toolbar sx={{ justifyContent: "space-between" }}>
        {/* // Logo or Title */}
        <Typography variant="h6" fontWeight="bold">
          Eventy
        </Typography>
        {/* // Logo or Title */}

        {/* // Navigation Links */}
        {isMobile ? (
          <>
            <IconButton color="inherit" onClick={handleDrawerToggle}>
              <MenuIcon />
            </IconButton>
            <Drawer
              anchor="left"
              open={drawerOpen}
              onClose={handleDrawerToggle}
            >
              <List sx={{ width: 200 }}>
                {["Home", "My Events", "My Calendar"].map((text) => {
                  const path =
                    text === "Home"
                      ? "/Home"
                      : text === "My Events"
                      ? "/MyEvents"
                      : "/MyCalendar";
                  return (
                    <ListItem
                      button
                      key={text}
                      component={Link}
                      to={path}
                      onClick={handleDrawerToggle}
                    >
                      <ListItemText primary={text} />
                    </ListItem>
                  );
                })}
              </List>
            </Drawer>
          </>
        ) : (
          <Box display="flex" gap={2}>
            <Button component={Link} to="/Home" color="inherit">
              Home
            </Button>
            <Button component={Link} to="/MyEvents" color="inherit">
              My Events
            </Button>
            <Button component={Link} to="/MyCalendar" color="inherit">
              My Calendar
            </Button>
          </Box>
        )}

        {/* // Navigation Links */}
        <Box display="flex" alignItems="center" gap={2}>
          <IconButton component={Link} to="/Cart">
            <Badge badgeContent={4} color="error">
              <ShoppingCart />
            </Badge>
          </IconButton>
          <Button
            component={Link}
            to="/Login"
            variant="outlined"
            color="inherit"
            sx={{
              borderRadius: 20,
              "&:hover": {
                backgroundColor: "blueviolet",
              },
            }}
          >
            Login
          </Button>
          <Button
            component={Link}
            to="/Login"
            variant="outlined"
            color="inherit"
            sx={{
              borderRadius: 20,
              "&:hover": {
                backgroundColor: "blueviolet",
              },
            }}
          >
            Sign Up
          </Button>
        </Box>
        {/* // Nav Bar */}
      </Toolbar>
    </AppBar>
  );
}
