import { Box, Button, Grid, Typography } from "@mui/material";

const categories = [
  "All",
  "Art",
  "Business",
  "Education",
  "Festival",
  "Food",
  "Nightlife",
  "Sports",
  "More",
];

const events = [
  {
    title: "Friendship Dinner",
    category: "Food",
    date: "March 17, 2024",
    location: "Istanbul",
    price: "$30",
    tag: "Showing",
    image: "../../../public/Images/133653917018572961.jpg", // replace with actual image
    description:
      "The Friendship Dinner is a heartwarming event hosted by Eventchamp...",
  },
  {
    title: "Table Mountain Cableway",
    category: "Travel",
    date: "April 14, 2024",
    location: "Cape Town",
    price: "$89",
    tag: "Showing",
    image: "../../../public/Images/133664391668906827.jpg",
    description:
      "Experience breathtaking views and unforgettable moments at the Table Mountain Cableway...",
  },
  // Add more events...
];

export default function Home() {
  return (
    <Box p={4}>
      <Typography variant="h4" align="center" fontWeight={700}>
        Upcoming <span style={{ color: "#fbbc04" }}>Events</span>
      </Typography>
      <Typography align="center" color="text.secondary" mt={1} mb={3}>
        Join us for thrilling events! Live music, workshops, art exhibits, and
        more!
      </Typography>

      <Box
        display="flex"
        justifyContent="center"
        flexWrap="wrap"
        gap={1}
        mb={4}
      >
        {categories.map((cat) => (
          <Button
            key={cat}
            variant="outlined"
            sx={{ borderRadius: 999, textTransform: "none", px: 2 }}
          >
            {cat}
          </Button>
        ))}
      </Box>

      <Grid container spacing={3}>
        {events.map((event, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Box
              borderRadius={2}
              overflow="hidden"
              boxShadow={2}
              bgcolor="#fff"
            >
              <img
                src={event.image}
                alt={event.title}
                width="100%"
                height="180px"
              />
              <Box p={2}>
                <Box display="flex" justifyContent="space-between" mb={1}>
                  <Button
                    size="small"
                    sx={{
                      bgcolor: "#fbbc04",
                      color: "#fff",
                      fontSize: "10px",
                      px: 1.5,
                    }}
                  >
                    {event.tag}
                  </Button>
                  <Button
                    size="small"
                    sx={{
                      bgcolor: "#fbbc04",
                      color: "#fff",
                      fontSize: "10px",
                      px: 1.5,
                    }}
                  >
                    {event.price}
                  </Button>
                </Box>
                <Typography variant="subtitle1" fontWeight={600}>
                  {event.title}
                </Typography>
                <Box
                  display="flex"
                  gap={1}
                  alignItems="center"
                  fontSize="12px"
                  mt={1}
                  color="text.secondary"
                >
                  <span>{event.category}</span> • <span>{event.date}</span> •{" "}
                  <span>{event.location}</span>
                </Box>
                <Typography variant="body2" mt={1} color="text.secondary">
                  {event.description}
                </Typography>
              </Box>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
