import React from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  IconButton,
  Badge,
  Button,
} from "@mui/material";
import { ShoppingCart } from "@mui/icons-material";
import {
  useTheme,
  useMediaQuery,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";

const theme = useTheme();
const isMobile = useMediaQuery(theme.breakpoints.down("md"));
const [drawerOpen, setDrawerOpen] = React.useState(false);

const handleDrawerToggle = () => {
  setDrawerOpen((prev) => !prev);
};

export default function Header1() {
  return (
    <AppBar position="static" sx={{ justifyContent: "space-between" }}>
      <Toolbar sx={{ justifyContent: "space-between" }}>
        {/* // Logo or Title */}
        <Typography variant="h6" fontWeight="bold">
          Eventy
        </Typography>
        {/* // Logo or Title */}

        {/* // Navigation Links */}
        {isMobile ? (
          <>
            <IconButton color="inherit" onClick={handleDrawerToggle}>
              <MenuIcon />
            </IconButton>
            <Drawer
              anchor="left"
              open={drawerOpen}
              onClose={handleDrawerToggle}
            >
              <List sx={{ width: 200 }}>
                {["Home", "My Events", "My Calendar"].map((text) => (
                  <ListItem button key={text} onClick={handleDrawerToggle}>
                    <ListItemText primary={text} />
                  </ListItem>
                ))}
              </List>
            </Drawer>
          </>
        ) : (
          <Box display="flex" gap={2}>
            <Button color="inherit">Home</Button>
            <Button color="inherit">My Events</Button>
            <Button color="inherit">My Calendar</Button>
          </Box>
        )}

        {/* // Navigation Links */}
        <Box display="flex" alignItems="center" gap={2}>
          <IconButton>
            <Badge badgeContent={4} color="error">
              <ShoppingCart />
            </Badge>
          </IconButton>
          <Button variant="outlined" color="inherit">
            Login
          </Button>
          <Button variant="contained" sx={{ backgroundColor: "blueviolet" }}>
            Sign Up
          </Button>
        </Box>
        {/* // Nav Bar */}
      </Toolbar>
    </AppBar>
  );
}
