import React from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  Button,
  Menu,
  MenuItem,
  IconButton,
  Badge,
} from "@mui/material";
import { ShoppingCart, ExpandMore } from "@mui/icons-material";
import { useState } from "react";

const menuItems = [
  { label: "Home", options: ["Main", "Landing"] },
  { label: "Events", options: ["All Events", "My Events"] },
  { label: "Calendar", options: ["Monthly", "Weekly"] },
  { label: "Speakers", options: ["Keynotes", "Workshops"] },
  { label: "Venues", options: ["Venue List"] },
  { label: "Schedules", options: ["Day 1", "Day 2"] },
  { label: "Pages", options: ["About", "Contact"] },
];

export default function Navbar() {
  const [anchorEls, setAnchorEls] = useState({});

  const handleOpen = (event, label) => {
    setAnchorEls({ ...anchorEls, [label]: event.currentTarget });
  };

  const handleClose = (label) => {
    setAnchorEls({ ...anchorEls, [label]: null });
  };

  return (
    <AppBar
      position="static"
      sx={{ backgroundColor: "gren", color: "black", boxShadow: "none" }}
    >
      <Toolbar sx={{ justifyContent: "space-between" }}>
        {/* Logo */}
        <Typography variant="h6" fontWeight="bold">
          EVENTCHAMP
        </Typography>

        {/* Nav Links */}
        <Box sx={{ display: "flex", gap: 2 }}>
          {menuItems.map(({ label, options }) => (
            <Box key={label}>
              <Button
                onClick={(e) => handleOpen(e, label)}
                endIcon={<ExpandMore />}
                sx={{ color: "black" }}
              >
                {label}
              </Button>
              <Menu
                anchorEl={anchorEls[label]}
                open={Boolean(anchorEls[label])}
                onClose={() => handleClose(label)}
              >
                {options.map((option) => (
                  <MenuItem key={option} onClick={() => handleClose(label)}>
                    {option}
                  </MenuItem>
                ))}
              </Menu>
            </Box>
          ))}
        </Box>

        {/* Cart and Auth Buttons */}
        <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
          <IconButton>
            <Badge badgeContent={0} color="error">
              <ShoppingCart />
            </Badge>
          </IconButton>
          <Button variant="outlined" sx={{ borderRadius: 5 }}>
            Sign In
          </Button>
          <Button
            variant="contained"
            sx={{ borderRadius: 5, backgroundColor: "blueviolet" }}
          >
            Sign Up
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
}
