import { Box, Button, Grid, Typography } from "@mui/material";
import EventCard from "./EventCard";

const categories = [
  "All",
  "Art",
  "Business",
  "Education",
  "Festival",
  "Food",
  "Nightlife",
  "Sports",
  "More",
];

const events = [
  {
    title: "Friendship Dinner",
    category: "Food",
    date: "March 17, 2024",
    location: "Istanbul",
    price: "$30",
    tag: "Showing",
    image: "/Images/133653917018572961.jpg", // replace with actual image
    description:
      "The Friendship Dinner is a heartwarming event hosted by Eventchamp...",
  },
  {
    title: "Table Mountain Cableway",
    category: "Travel",
    date: "April 14, 2024",
    location: "Cape Town",
    price: "$89",
    tag: "Showing",
    image: "/Images/133664391668906827.jpg",
    description:
      "Experience breathtaking views and unforgettable moments at the Table Mountain Cableway...",
  },
  // Add more events...
];

export default function Home() {
  return (
    <Box p={4}>
      <Typography variant="h4" align="center" fontWeight={700}>
        Upcoming <span style={{ color: "#fbbc04" }}>Events</span>
      </Typography>
      <Typography align="center" color="text.secondary" mt={1} mb={3}>
        Join us for thrilling events! Live music, workshops, art exhibits, and
        more!
      </Typography>

      {/* Categories */}

      <Box
        gap="10px"
        display="flex"
        justifyContent="center"
        mb={4}
        flexWrap="wrap"
      >
        {categories.map((cat) => (
          <Button
            key={cat}
            variant="outlined"
            sx={{ borderRadius: 999, textTransform: "none", px: 2 }}
          >
            {cat}
          </Button>
        ))}
      </Box>

      <Grid container spacing={3}>
        {events.map((event, index) => (
          <Grid item xs={8} sm={6} md={4} key={index}>
            <Box height="100%">
              <EventCard event={event} />
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
