import "./LoanForm.css";
import SubmitMessage from "./SubmitMessage";
import { useState } from "react";

export default function LoanForm() {
  const [inputs, setInputs] = useState({
    name: "",
    age: "",
    phone: "",
    employee: false,
    loanAmount: "",
  });

  const [ShowSubmitMessage, setShowSubmitMessage] = useState(false);
  function HandleSubmit(event) {
    event.preventDefault();
    setShowSubmitMessage(true);
  }

  const isFormInvalid =
    inputs.name === "" ||
    inputs.age === "" ||
    inputs.phone === "" ||
    inputs.loanAmount === "";
  function handleClick(event) {
    // prevent the click from hiding the message if it's inside the form
    if (
      event.target.closest("form") ||
      event.target.tagname.toLowerCase === "form"
    )
      return;

    setShowSubmitMessage(false);
  }

  return (
    <div
      className="LoanForm"
      onClick={handleClick}
      style={{ background: "red" }}
    >
      <form>
        <h1>Request a Loan</h1>
        <hr />

        <div className="inputs">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Enter your name"
            required
            value={inputs.name}
            onChange={(event) => {
              setInputs({ ...inputs, name: event.target.value });
            }}
          />

          <label htmlFor="age">Age:</label>
          <input
            type="number"
            id="age"
            name="age"
            placeholder="Enter your age"
            required
            value={inputs.age}
            onChange={(event) => {
              setInputs({ ...inputs, age: event.target.value });
            }}
          />

          <label htmlFor="phone">Phone:</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            placeholder="Enter your phone number"
            required
            value={inputs.phone}
            onChange={(event) => {
              setInputs({ ...inputs, phone: event.target.value });
            }}
          />

          <div className="checkboxRow">
            <label htmlFor="employee">Are you an employee?</label>
            <input
              className="isEmployee"
              type="checkbox"
              id="employee"
              name="employee"
              checked={inputs.employee}
              onChange={(event) => {
                setInputs({ ...inputs, employee: event.target.checked });
              }}
            />
          </div>

          <label htmlFor="loanAmount">Loan Amount:</label>
          <select
            id="loanAmount"
            name="loanAmount"
            required
            value={inputs.loanAmount}
            onChange={(event) => {
              setInputs({ ...inputs, loanAmount: event.target.value });
            }}
          >
            <option value="">Select an amount</option>
            <option value="1000">1000</option>
            <option value="2000">2000</option>
            <option value="3000">3000</option>
            <option value="4000">4000</option>
          </select>

          <button
            onClick={HandleSubmit}
            type="submit"
            disabled={isFormInvalid}
            className={isFormInvalid ? "disabled" : ""}
          >
            Submit
          </button>
        </div>
      </form>
      <SubmitMessage isVisible={ShowSubmitMessage} />
    </div>
  );
}
